import React, { useState, useEffect } from "react";
import PostitDataService from "../services/postit";
import { Link } from "react-router-dom";

const PostitsList = props => {
    const [postits, setPostits] = useState([]);
    const [searchName, setSearchName ] = useState("");

  useEffect(() => {
    retrievePostits();
  }, []);


  const retrievePostits = () => {
    PostitDataService.getAll()
      .then(response => {
        console.log(response.data);
        setPostits(response.data.postits);    
      })
      .catch(e => {
        console.log(e);
      });
  };

  const onChangeSearchName = e => {
    const searchName = e.target.value;
    setSearchName(searchName);
  };

  const find = (query, by) => {
    PostitDataService.find(query, by)
      .then(response => {
        console.log(response.data);
        setPostits(response.data.postits);
      })
      .catch(e => {
        console.log(e);
      });
  };
  const findByName = () => {
    find(searchName, "name")
  };


  return (
    <div>
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchName}
            onChange={onChangeSearchName}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findByName}
            >
              Search
            </button>
          </div>
        </div>

      </div>
      <div className="row">
        {postits.map((postit) => {
          const content = `${postit.content}`;
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{postit.title}</h5>
                  <p className="card-text">
                    <strong>Content: </strong>{content}
                    <strong>Deadline: </strong>{postit.deadline}<br/>
                  </p>
 
                </div>
              </div>
            </div>
          );
        })}


      </div>
    </div>
  );
};

export default PostitsList;
