import React, { useState, useEffect } from "react";
import PostitsDataService from "../services/show-postits";
import { Link } from "react-router-dom";

const PostitsList = (props) => {
  const [postits, setPostits] = useState([]);
  const [postit, setPostit] = useState([]);

  useEffect(() => {
    retrieveAllPostits();
    retrieveOnePostit();
  }, []);

  //Retrieve all postits to display them client side
  const retrieveAllPostits = () => {
    PostitsDataService.getAll()
      .then((response) => {
        //Getting the data requested from the server
        setPostits(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

//Retrieve one postit to proceed the deletion
  const retrieveOnePostit = (id) => {
    PostitsDataService.get(id)
      .then((response) => {
        setPostit(response.data);
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };



  return (
    <div className="row">
      {
        //Mapping the data retrieved to the key postits and then display it
        postits.map((postit) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{postit.title}</h5>
                  <p className="card-text">
                    <strong>Content: </strong>
