import http from "../http-common";

class PostitDataService {
  getAll(page = 0) {
    return http.get(`postits?page=${page}`);
  }

  get(id) {
    return http.get(`/postit?id=${id}`);
  }

  find(query, by = "name", page = 0) {
    return http.get(`postit?${by}=${query}&page=${page}`);
  } 

  createPostit(data) {
    return http.post("/postit-new", data);
  }

  updatePostit(data) {
    return http.put("/postit-edit", data);
  }

  deletePostit(id, userId) {
    return http.delete(`/postit-delete?id=${id}`, {data:{user_id: userId}});
  }

}

export default new PostitDataService();
