import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import AddPostit from "./components/add-postits";
import Postit from "./components/postits";
import PostitsList from "./components/postits-list";


function App() {
  const [user, setUser] = React.useState(null);
  return (
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-dark">
        <a href="/restaurants" className="navbar-brand">
          Restaurant Reviews
        </a>
        <div className="navbar-nav mr-auto">
          <li className="nav-item">
            <Link to={"/restaurants"} className="nav-link">
              Restaurants
            </Link>
          </li>
          <li className="nav-item" >
            { user ? (
              <a onClick={logout} className="nav-link" style={{cursor:'pointer'}}>
                Logout {user.name}
              </a>
            ) : (            
            <Link to={"/login"} className="nav-link">
              Login
            </Link>
            )}

          </li>
        </div>
      </nav>

      <div className="container mt-3">
        <Switch>
          <Route exact path={["/", "/postits"]} component={PostitsList} />
          <Route 
            path="/postits/:id/postit"
            render={(props) => (
              <AddPostit {...props} user={user} />
            )}
          />
          <Route 
            path="/postits/:id"
            render={(props) => (
              <Postit {...props} user={user} />
            )}
          />
        </Switch>
      </div>
    </div>
  );   
}

export default App;
