import React, { useState, useEffect } from "react";
import PostitsDataService from "../services/show-postits";
import { Link } from "react-router-dom";

const PostitsList = (props) => {
  const [postits, setPostits] = useState([]);
  const [postit, setPostit] = useState([]);

  //Retrieve all postits to display them client side
  const retrieveAllPostits = () => {
    PostitsDataService.getAll()
      .then((response) => {
        //Getting the data requested from the server and setting them into the client
        setPostits(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    retrieveAllPostits();
  }, []);

  //Retrieve one postit to proceed the deletion
  const retrieveOnePostit = (id) => {
    PostitsDataService.get(id)
      .then((response) => {
        setPostit(response.data._id);
        console.log(response.data.id);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  
  useEffect(() => {
    retrieveOnePostit();
  }, [props.match.params.id]);

  const deletePostit = (postitId, index) => {
    PostitsDataService.deletePostit(postitId).then(() => {
      setPostit((prevState) => {
        prevState.splice(index, 1);
        return({
          ...prevState
        })
      })
    }).catch(e => {
      console.log(e);
    })
  };

  return (
    <div className="row">
      {
        //Mapping the data retrieved to the key postits and then display it
        postits.map((postit, index) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{postit.title}</h5>
                  <p className="card-text">
                    <strong>Content: </strong>
                    {postit.content}
                    <br />
                    <strong>Deadline: </strong>
                    {postit.deadline}
                  </p>
                  <div className="row">
                    <a
                      onClick={() => deletePostit(postit._id, index)}
                      className="btn btn-primary col-lg-5 mx-1 mb-1"
                    >
                      Delete
                    </a>
                  </div>
                </div>
              </div>
            </div>
          );
        })
      }
    </div>
  );
};

export default PostitsList;
