import React, { useState, useEffect } from "react";
import PostitDataService from "../services/postit";
import { Link } from "react-router-dom";

const Postit = props => {
  const initialPostitState = {
    id: null,
    title: "",
    content: "",
    deadline: ""
  };
  const [postit, setPostit] = useState(initialPostitState);

  const getPostit = id => {
    PostitDataService.get(id)
      .then(response => {
        setPostit(response.data);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  useEffect(() => {
    getPostit(props.match.params.id);
  }, [props.match.params.id]);

  const deletePostit = (postitId, index) => {
    PostitDataService.deletePostit(postitId, props.user.id)
      .then(response => {
        setPostit((prevState) => {
          prevState.reviews.splice(index, 1)
          return({
            ...prevState
          })
        })
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div>
        <div>
          <h5>{postit.title}</h5>
          <p>
            <strong>content: </strong>{postit.content}<br/>
            <strong>DeadLine: </strong>{postit.deadline}<br/>
          </p>
          <Link to={"/postits/" + props.match.params.id + "/postit"} className="btn btn-primary">
            Add Postit
          </Link>
          <h4> Postits </h4>
          <div className="row">
            {postit.length > 0 ? (
             postit.map((review, index) => {
               return (
                 <div className="col-lg-4 pb-1" key={index}>
                   <div className="card">
                     <div className="card-body">
                       {props.user && props.user.id === review.user_id &&
                          <div className="row">
                            <a onClick={() => deletePostit(postit._id, index)} className="btn btn-primary col-lg-5 mx-1 mb-1">Delete</a>
                            <Link to={{
                              pathname: "/postits/" + props.match.params.id + "/postit",
                              state: {
                                currentPostit: postit
                              }
                            }} className="btn btn-primary col-lg-5 mx-1 mb-1">Edit</Link>
                          </div>                   
                       }
                     </div>
                   </div>
                 </div>
               );
             })
            ) : (
            <div className="col-sm-4">
              <p>No postits yet.</p>
            </div>
            )}

          </div>
        </div>
    </div>
  );
};

export default Postit;
