import React, { useState, useEffect } from "react";
import PostitsDataService from "../services/show-postits";
import { Link } from "react-router-dom";


const PostitsList = props => {
  const [postits, setPostits] = useState([]);

  useEffect(() => {
    retrievePostits();
  }, []);

  const retrievePostits = () => {
    PostitsDataService.getAll()
      .then((response) => {
        setPostits(response.data.postits);
      })
      .catch((e) => {
        console.log(e);
      });
  };



  return (
    <div className="row">
    {(postit) => {
      console.log("===> "+postit)
      //const address = `${restaurant.address.building} ${restaurant.address.street}, ${restaurant.address.zipcode}`;
      return (
        <div className="col-lg-4 pb-1">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{postit.title}</h5>
              <p className="card-text">
                <strong>Content: </strong>{postit.content}<br/>
                <strong>Deadline: </strong>{postit.deadline}
              </p>
              <div className="row">
              {/* <Link to={"/restaurants/"+restaurant._id} className="btn btn-primary col-lg-5 mx-1 mb-1">
                View Reviews
              </Link> */}
              </div>
            </div>
          </div>
        </div>
      );
    }}


  </div>
  );
};

export default PostitsList;
