import React, { useState, useEffect } from "react";
import PostitsDataService from "../services/show-postits";
import { Link } from "react-router-dom";

const EditPostit = (props) => {

 const [postit, setPostit] = useState([])
 const [submitted, setSubmitted] = useState(false);

 const [postitTitle, setPostitTitle] = useState(props.match.params.title);
 const [postitContent, setPostitContent] = useState(props.match.params.content);
 const [postitDeadline, setPostitDeadline] = useState(
  props.match.params.deadline
 );

 const handleInputTitle = (event) => {
   setPostitTitle(event.target.value);
 };
 const handleInputContent = (event) => {
   setPostitContent(event.target.value);
 };
 const handleInputDeadline = (event) => {
   setPostitDeadline(event.target.value);
 };


  // const getPostit = (id) => {
  //   PostitsDataService.get(id)
  //     .then((response) => {
  //       //Getting the data requested from the server and setting them into the client
  //       setPostit(response.data);
  //       console.log("ANY ID ? ====>" + response.data);
  //       return (response.data);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };

  const savePostit = () => {
    var data = {
      title: postitTitle,
      content: postitContent,
      deadline: postitDeadline,
    };

    data.postit_id= props.match.params.id;
    PostitsDataService.updatePostit(data)
      .then((response) => {
        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <div className="submit-form">
      {submitted ? (
        <div>
          <h4>You submitted successfully!</h4>
        </div>
      ) : (
        <div>
          <div className="form-group">
            <label htmlFor="description"></label>
            <input
              type="text"
              className="form-control"
              id="title"
              required
              value={props.match.params.title}
              onChange={handleInputTitle}
              name="text"
            />
            <input
              type="text"
              className="form-control"
              id="content"
              required
              value={ props.match.params.content}
              onChange={handleInputContent}
              name="content"
            />
            <input
              type="text"
              className="form-control"
              id="deadline"
              required
              value={ props.match.params.deadline}
              onChange={handleInputDeadline}
              name="deadline"
            />
          </div>
          <Link to="/" className="btn btn-success">
            <button onClick={savePostit} className="btn btn-success" to="/">
              Submit
            </button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default EditPostit;
