import React, { useState, useEffect } from "react";
import PostitsDataService from "../services/show-postits";
import { Link } from "react-router-dom";

const PostitsList = (props) => {
  const [postits, setPostits] = useState([]);
  const [postit, setPostit] = useState([]);

  //Retrieve all postits to display them client side
  const retrieveAllPostits = (props) => {
    PostitsDataService.getAll()
      .then((response) => {
        //Getting the data requested from the server and setting them into the client
        setPostits(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    retrieveAllPostits();
  }, []);

  // //Retrieve one postit
  // const retrieveOnePostit = (id) => {
  //   PostitsDataService.get(id)
  //     .then((response) => {
  //       setPostit(response.data._id);
  //       console.log(response.data.id);
  //     })
  //     .catch((e) => {
  //       console.log(e);
  //     });
  // };

  // useEffect(() => {
  //   retrieveOnePostit();
  // }, [props.match.params.id]);

  const deletePostit = (postitId) => {
    PostitsDataService.deletePostit(postitId).catch((e) => {
      console.log(e);
    });
  };

  const editPostit = (data, postitId) => {
    PostitsDataService.updatePostit(data, postitId).catch((e) => {
      console.log(e);
    });
  };

  //HANDELING CHANGES

  let initialPostitStateTitle = "";
  let initialPostitStateContent = "";
  let initialPostitStateDeadline = "";
  let editing = false;

  if (props.location.state && props.location.state.currentPostit) {
    editing = true;
    initialPostitStateTitle = props.location.state.currentPostit.title;
    initialPostitStateContent = props.location.state.currentPostit.content;
    initialPostitStateDeadline = props.location.state.currentPostit.deadline;
  }

  const [postitTitle, setPostitTitle] = useState(initialPostitStateTitle);
  const [postitContent, setPostitContent] = useState(initialPostitStateContent);
  const [postitDeadline, setPostitDeadline] = useState(
    initialPostitStateDeadline
  );
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (event) => {
    setPostitTitle(event.target.value);
    setPostitContent(event.target.value);
    setPostitDeadline(event.target.value);
  };

  const savePostit = () => {
    var data = {
      title: postitTitle,
      content: postitContent,
      deadline: postitDeadline,
    };

    if (editing) {
      data.postit_id = props.state.currentPostit._id;
      PostitsDataService.updatePostit(data)
        .then((response) => {
          setSubmitted(true);
          console.log(response.data);
        })
        .catch((e) => {
          console.log(e);
        });
    } else {
      PostitsDataService.createPostit(data)
        .then((response) => {
          setSubmitted(true);
          console.log(response.data);
        })
        .catch((e) => {
          console.log(e);
        });
    }
  };
  return (
    <div className="row">
      {
        //Mapping the data retrieved to the key postits and then display it
        postits.map((postit, index) => {
          return (
            <div className="col-lg-4 pb-1">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{postit.title}</h5>
                  <p className="card-text">
                    <strong>Content: </strong>
                    {postit.content}
                    <br />
                    <strong>Deadline: </strong>
                    {postit.deadline}
                  </p>
                  <div className="row">
                    <a
                      onClick={() => {deletePostit(postit._id); 
                        window.location.reload();}}
                      className="btn btn-primary col-lg-5 mx-1 mb-1"
                    >
                      Delete   
                    </a>
                    <div className="submit-form">
                      {submitted ? (
                        <div>
                          <h4>You submitted successfully!</h4>
                          <Link
                            to={window.location.reload()}
                            className="btn btn-success"
                          >
                            Back to liste of Postits
                          </Link>
                        </div>
                      ) : (
                        <div>
                          <div className="form-group">
                            <label htmlFor="description">
                              {editing ? "Edit" : "Create"} Edit / Create
                            </label>
                            <input
                              type="text"
                              className="form-control"
                              id="title"
                              required
                              value={postitTitle}
                              onChange={handleInputChange}
                              name="text"
                            />
                            <input
                              type="text"
                              className="form-control"
                              id="content"
                              required
                              value={postitContent}
                              onChange={handleInputChange}
                              name="content"
                            />
                            <input
                              type="text"
                              className="form-control"
                              id="deadline"
                              required
                              value={postitDeadline}
                              onChange={handleInputChange}
                              name="deadline"
                            />
                          </div>
                          <button
                            onClick={savePostit}
                            className="btn btn-success"
                          >
                            Submit
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })
      }
    </div>
  );
};

export default PostitsList;
